import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const BudgetDashboard = () => {
  const [budgets, setBudgets] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchBudgets = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/api/budget', {
          headers: { 'x-auth-token': token }
        });
        setBudgets(res.data);
        setLoading(false);
      } catch (err) {
        console.error(err);
        setLoading(false);
      }
    };

    fetchBudgets();
  }, []);

  const chartData = {
    labels: budgets.map(budget => budget.category),
    datasets: [
      {
        label: 'Budget Amount',
        data: budgets.map(budget => budget.amount),
        backgroundColor: 'rgba(54, 162, 235, 0.5)',
      },
      {
        label: 'Spent Amount',
        data: budgets.map(budget => budget.spent),
        backgroundColor: 'rgba(255, 99, 132, 0.5)',
      },
    ],
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="container mx-auto px-4 py-8">
      <h2 className="text-2xl font-bold mb-6">Budget Overview</h2>
      <div className="bg-white p-6 rounded-lg shadow-lg">
        <Bar data={chartData} />
      </div>
      
      <div className="mt-8 grid grid-cols-1 md:grid-cols-2 gap-6">
        {budgets.map(budget => (
          <div key={budget._id} className="bg-white p-6 rounded-lg shadow">
            <h3 className="text-xl font-semibold mb-2">{budget.category}</h3>
            <div className="flex justify-between mb-2">
              <span>Budget:</span>
              <span>${budget.amount}</span>
            </div>
            <div className="flex justify-between mb-4">
              <span>Spent:</span>
              <span>${budget.spent}</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2.5">
              <div
                className={`h-2.5 rounded-full ${
                  budget.spent > budget.amount ? 'bg-red-600' : 'bg-green-600'
                }`}
                style={{
                  width: `${Math.min((budget.spent / budget.amount) * 100, 100)}%`,
                }}
              ></div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BudgetDashboard;
