import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import { StarIcon } from '@heroicons/react/24/outline';

const StockCard = ({ symbol }) => {
  const [stockData, setStockData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStockData = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get(`http://localhost:5000/api/stocks?symbol=${symbol}`, {
          headers: { 'x-auth-token': token }
        });
        setStockData(res.data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchStockData();
  }, [symbol]);

  const addToFavorites = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5000/api/stocks/favorites', 
        { symbol },
        { headers: { 'x-auth-token': token } }
      );
    } catch (err) {
      console.error('Error adding to favorites:', err);
    }
  };

  if (loading) {
    return (
      <div className="animate-pulse bg-white p-6 rounded-lg shadow-lg">
        <div className="h-4 bg-gray-200 rounded w-3/4 mb-4"></div>
        <div className="h-8 bg-gray-200 rounded w-1/2 mb-4"></div>
        <div className="h-4 bg-gray-200 rounded w-full"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 p-6 rounded-lg shadow-lg">
        <p className="text-red-500">Error: {error}</p>
      </div>
    );
  }

  const chartData = {
    labels: stockData.historical.map(day => new Date(day.date).toLocaleDateString()),
    datasets: [{
      label: 'Stock Price',
      data: stockData.historical.map(day => day.price),
      borderColor: 'rgb(75, 192, 192)',
      tension: 0.1
    }]
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-lg">
      <div className="flex justify-between items-start mb-4">
        <div>
          <h3 className="text-xl font-semibold">{stockData.name}</h3>
          <p className="text-gray-500">{stockData.symbol}</p>
        </div>
        <button
          onClick={addToFavorites}
          className="text-gray-400 hover:text-yellow-500"
        >
          <StarIcon className="h-6 w-6" />
        </button>
      </div>

      <div className="mb-4">
        <span className="text-3xl font-bold">${stockData.price.toFixed(2)}</span>
        <span className={`ml-2 ${
          stockData.change >= 0 ? 'text-green-500' : 'text-red-500'
        }`}>
          {stockData.change.toFixed(2)}%
        </span>
      </div>

      <div className="mb-6">
        <div className={`inline-block px-3 py-1 rounded-full text-sm font-semibold ${
          stockData.suggestion === 'BUY' ? 'bg-green-100 text-green-800' :
          stockData.suggestion === 'SELL' ? 'bg-red-100 text-red-800' :
          'bg-yellow-100 text-yellow-800'
        }`}>
          {stockData.suggestion}
        </div>
      </div>

      <div className="h-48">
        <Line data={chartData} options={{ maintainAspectRatio: false }} />
      </div>
    </div>
  );
};

export default StockCard;
